SELECT st.name AS student_name, co.title AS course_title
FROM student AS st CROSS JOIN course AS co
WHERE st.dept_name = co.dept_name;

SELECT id, course_id, sec_id, semester, year FROM takes WHERE grade = 'A'
UNION
SELECT id, course_id, sec_id, semester, year FROM takes WHERE grade = 'B';

SELECT s.course_id, s.sec_id, s.semester, s.year, t.day, t.start_hr, t.start_min
FROM section AS s
JOIN time_slot AS t ON s.time_slot_id = t.time_slot_id;

SELECT course_id, sec_id FROM section WHERE semester = 'Fall' AND year = 2009
EXCEPT
SELECT course_id, sec_id FROM section WHERE semester = 'Spring' AND year = 2010;

SELECT inst.name AS instructor_name, inst.dept_name, inst.salary
FROM instructor AS inst
WHERE inst.salary > 90000;

SELECT t1.id
FROM takes AS t1
WHERE t1.grade = 'A' AND EXISTS (SELECT 1 FROM takes AS t2 WHERE t2.id = t1.id AND t2.grade = 'B');

SELECT id, course_id, sec_id, semester, year FROM takes
EXCEPT
SELECT id, course_id, sec_id, semester, year FROM takes WHERE grade = 'F';

SELECT dept_name FROM department
EXCEPT
SELECT dept_name FROM course;
